import { FormGroup, InputGroup } from "@blueprintjs/core"
import { Button } from "@blueprintjs/core";

function Form(props) {
  return(
    // <div>
    //   <FormGroup
    //     helperText="Helper text with details..."
    //     label="Label A"
    //     labelFor="text-input"
    //     labelInfo="(required)"
    //   >
    //     <InputGroup id="text-input" placeholder="Placeholder text" />
    //   </FormGroup>
    // </div>

    <form onSubmit={props.handleSubmit}>

        <h2>Add To Do Item</h2>

        <label>
          <span>To Do Item</span>
          <InputGroup onChange={props.handleChange} name="text" type="text" placeholder="Item Details"></InputGroup>
          {/* <input onChange={props.handleChange} name="text" type="text" placeholder="Item Details" /> */}
        </label>

        <label>
          <span>Assigned To</span>
          <input onChange={props.handleChange} name="assignee" type="text" placeholder="Assignee Name" />
        </label>

        <label>
          <span>Difficulty</span>
          <input onChange={props.handleChange} defaultValue={3} type="range" min={1} max={5} name="difficulty" />
        </label>

        <label>
          <Button type="submit">Add item</Button>
          {/* <button type="submit">Add Item</button> */}
        </label>
      </form>
  )
}

export default Form;
